import { useState } from "react"


const ProductForm = () => {
    const [id, setId] = useState('');
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [error, setError] = useState(null);
    const submitHandler = async (x) => {
        x.preventDefault();
        const product = { id, name, price, description }
        const response = await fetch('', {
            method: 'POST',
            body: JSON.stringify(product),
            headers: {
                'Content-type': 'application/json'
            }
        })
        const json = await response.json()
        if (!response.ok) {
            setError(json.error);
        }
        if (!response.ok) {
            setError(null);
            console.log('new product was added', json);
            setId('');
            setName('');
            setPrice('');
            setDescription('');
        }
    }

    return (
        <form className="create" onSubmit={submitHandler()}>
            <h3>Add new product</h3>

            <label>ID:</label>
            <input type="text" onChange={(x) => { setId(x.target.value) }} value={id}></input>

            <label>Name:</label>
            <input type="text" onChange={(x) => { setName(x.target.value) }} value={name}></input>

            <label>Price:</label>
            <input type="number" onChange={(x) => { setPrice(x.target.value) }} value={price}></input>

            <label>Description:</label>
            <input type="text" onChange={(x) => { setDescription(x.target.value) }} value={description}></input>

<button type="submit">Create</button>

            {error && <div className="error">{error}</div>}
        </form>
    );
}
export default ProductForm;
