import { useState } from "react"

const ProductForm = () => {
    const [id, setId] = useState('');
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [quantity, setQuantity] = useState('');
    const [description, setDescription] = useState('');
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);
    
    const submitHandler = async (x) => {
        x.preventDefault();
        const product = { id, name, price, description };
        const response = await fetch('/api/products', {
            method: 'POST',
            body: JSON.stringify(product),
            headers: {
                'Content-type': 'application/json'
            }
        })
        const item = await response.json();
        if (!response.ok) {
            setError(item.error);
        }
        if (response.ok) {
            setError(null);
            console.log('new product was added', item);
            setId('');
            setName('');
            setPrice('');
            setDescription('');
            setSuccess(`New product by the name ${name} was created.`)
        }
    }

    return (
        <form className="create" onSubmit={submitHandler}>
            <h3>Add new product</h3>

            <label>ID: </label>
            <input type="text" onChange={(x) => { setId(x.target.value) }} value={id}></input>
            <br />
            <label>Name: </label>
            <input type="text" onChange={(x) => { setName(x.target.value) }} value={name}></input>
            <br />
            <label>Price: </label>
            <input type="number" onChange={(x) => { setPrice(x.target.value) }} value={price}></input>
            <br />
            <label>Quantity: </label>
            <input type="number" onChange={(x) => { setQuantity(x.target.value) }} value={quantity}></input>
            <br />
            <label>Description: </label>
            <input type="text" onChange={(x) => { setDescription(x.target.value) }} value={description}></input>
            <br />
            
            <button >Create Product</button>
{suc}
            {error && <div className="error">{error}</div>}
        </form>
    );
}
export default ProductForm;
